  private void onHide(java.awt.event.ActionEvent evt)//GEN-FIRST:event_onHide
  {//GEN-HEADEREND:event_onHide
    rm.updateNumCols(2);
  }//GEN-LAST:event_onHide

  private void onAdd(java.awt.event.ActionEvent evt)//GEN-FIRST:event_onAdd
  {//GEN-HEADEREND:event_onAdd
    rm.updateNumCols(3);
  }//GEN-LAST:event_onAdd

  private void onAddData(java.awt.event.ActionEvent evt)//GEN-FIRST:event_onAddData
  {//GEN-HEADEREND:event_onAddData
    RadioDialog rd = new RadioDialog(this, true);
    rd.setVisible(true);
    Radio radio = rd.getRadio();
    rm.add(radio);
  }//GEN-LAST:event_onAddData

